import {Paragraphs} from './Text'
import {Smallmap} from './Smallmap'
export default function Map() {
  const [count,setCount] = useState(0)
  const [text,setText] = useState([])


const ok = () => {
let newArray =  Paragraphs.filter(x=>x.id <= count)
setText(newArray.map(c=>{
  const {id,object} = c
  return <div key={id}>
  <p>{object}</p>
  </div>
}))
}



  return (
    <div className='container bg-success text-light text-center'>
      <div></div>
  <h1>Our Paragraphs</h1>
  <hr />
<div className='d-flex align-items-center justify-content-center'>
  <button className='btn bg-info p-3 m-3' onClick={()=>ok()}>Create</button>
  <input value={count} onChange={(e)=>setCount(e.target.value)}  className='bg-dark rounded text-light text-center'  type='number' style={{
    width:'50px',
    height:'55px'
  }}/>
</div>
    <>
  {text}
  </>
 </div>
  )
}
